style.content:
html {
	font-family: Arial, Helvetica, sans-serif;
  background-color: #555;
}
#square {
	display: flex;
	justify-content: center;
	align-items: center;
  font-size: 100px;
  color: white;
  margin-bottom: 10%;
}
#btn {
  padding: 10px;
  display: block;
  margin: auto;
  font-size: larger;
}
p {
  color: white;
  font-size: larger;
  margin-bottom: 20%;
}

script.content:
let arr = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']

function rand(max){
	return Math.ceil(Math.random() * max)
}

function doThingy(){
  let a = 1
  let b = 1

  a = rand(7)
  b = rand(7)
  console.log(arr[a]+b)
  document.querySelector('#square').innerText = arr[a]
  document.querySelector('#square').innerText += b
}
document.querySelector('#btn').onclick = function(){
  doThingy()
}
doThingy()
